    private void trianguloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_trianguloActionPerformed
    String n = n1.getText();
    String n3 = n2.getText();
    double nn,r;
    double m;
    nn=Double.parseDouble(n);//base
     r = Double.parseDouble(n3);//altura
     m= Triangulo.areaT(nn,r);
    txtResul.setText(" "+m);

    }//GEN-LAST:event_trianguloActionPerformed

