    private void btntrapActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btntrapActionPerformed
      String n = n1.getText();
      String n3 = n2.getText();
       String n7 = N3.getText();
      double nn,r,m,u;
      nn=Double.parseDouble(n);//Base menor
      r = Double.parseDouble(n3);//Base mayor
      u = Double.parseDouble(n7);//altura
      m= Trapecio.areaTrap(nn,r,u);
      txtResul.setText(" "+m);
    }//GEN-LAST:event_btntrapActionPerformed

